    public static ArrayList<Isc19> getListData() {
        ArrayList<Isc19> list = new ArrayList<>();
        for (int position = 0; position < isc19Titles.length; position++) {
            Isc19 isc19 = new Isc19();
            isc19.setTitle(isc19Titles[position]);
            isc19.setDate(isc19Dates[position]);
            isc19.setSiteSource(isc19Sites[position]);
            isc19.setCategory(isc19Categories[position]);
            isc19.setWriter(isc19Writers[position]);
            isc19.setDetail(isc19Details[position]);
            isc19.setThumbnail(isc19Thumbnails[position]);
            list.add(isc19);
        }
        return list;
    }
